stylesheet:
QWidget {
    background-color: #E5EFEE;
    color: #000000;
}
QPushButton {
    background-color: #E5EFEE;
    border: 3px solid #28AFC2;
    padding: 5px;
    border-radius: 15px;
    font-weight: bold;
}
QPushButton:hover {
    background-color: #ffffff;
}
QPushButton:pressed {
    background-color: #B5E8EB;
}
QPushButton:disabled {
    background-color: #dcdcdc;
    color: #888888;
    border: 1px solid #b0c4de;
}
QFrame {
    background-color: #B5E8EB;
    border: 1px solid #28AFC2;
    border-radius: 12px;
}
QComboBox {
    background-color: #ffffff;
    border: 1px solid #28AFC2;
    border-radius: 8px;
    padding: 3px;
}
QComboBox::drop-down {
    border-left: 1px solid #28AFC2;
}
QComboBox QAbstractItemView {
    background-color: #ffffff;
    border: 1px solid #28AFC2;
    selection-background-color: #B5E8EB;
}
QDoubleSpinBox {
    background-color: #ffffff;
    border: 1px solid #28AFC2;
    border-radius: 8px;
    padding: 3px;
}
QLabel {
    background-color: transparent;
    border: none;
}
#total_weight_label {
    color: #28AFC2;
}
#status_label {
    color: #EB98F8;
    border-top: 1px solid #B5E8EB;
    font-weight: bold;
}
QScrollArea {
    border: none;
}
QScrollBar:vertical {
    background: #E5EFEE;
    width: 12px;
    margin: 0px;
    border-radius: 6px;
}
QScrollBar::handle:vertical {
    background: #69E72E;
    min-height: 20px;
    border-radius: 6px;
    border: 1px solid #58c024;
}
QScrollBar::handle:vertical:hover {
    background: #8aff57;
}
QScrollBar::add-line:vertical, QScrollBar::sub-line:vertical {
    background: none;
    height: 0px;
    subcontrol-position: top;
    subcontrol-origin: margin;
}
